// WelcomeContent.js
import * as React from 'react';

export default class WelcomeContent extends React.Component {
  render() {
    const { onRoleSelect } = this.props; // Receive the onRoleSelect prop

    return (
      <div className="row justify-content-md-center">
        <div className="jumbotron jumbotron-fluid">
          <h1 className="display-4">Welcome Collaborative Code Editor</h1>
          <h2 className="lead">Choose your role:</h2>
          <div className="mt-4">
            <button className="btn btn-primary mx-2" onClick={() => onRoleSelect('admin')}>Admin</button>
            <button className="btn btn-secondary mx-2" onClick={() => onRoleSelect('editor')}>Editor</button>
            <button className="btn btn-success mx-2" onClick={() => onRoleSelect('viewer')}>Viewer</button>
          </div>
        </div>
      </div>
    );
  }
}
