// src/components/JoinSession.js
import React, { useState, useEffect } from 'react';
import '../../CSS/JoinSession.css'; // Import the CSS file
import { useNavigate } from 'react-router-dom';

const JoinSession = () => {
  const [sessionCode, setSessionCode] = useState('');  // Room ID
  const [projectName, setProjectName] = useState('');  // Project Name
  const [password, setPassword] = useState('');         // Room Password
  const username = localStorage.getItem('username');    // Username from localStorage
  const navigate = useNavigate(); 

  const handleJoinSession = () => {
    console.log('Joined session:', sessionCode);
    console.log('Password:', password);
    console.log('Project Name:', projectName);
    console.log('Username:', username);

    navigate('/collaboratice-room'); // Navigate to the next page
  };


  return (
    <div className="join-session-container"> 
      <h2>Join a Room</h2>
      <input
        type="text"
        placeholder="Enter room ID"
        value={sessionCode}
        onChange={(e) => {setSessionCode(e.target.value) && localStorage.setItem('sessionCode', sessionCode) // Store the username
        }}
      />
      <input
        type="password"
        placeholder="Enter room password"
        value={password}
        onChange={(e) => { setPassword(e.target.value) && localStorage.setItem('password', password) } }
      />
      <input
        type="text"
        placeholder="Enter project name"
        value={projectName}
        onChange={(e) => setProjectName(e.target.value) && localStorage.setItem('projectName', projectName)} // Fixed function name
      />
      <button onClick={handleJoinSession}>Join Session</button>
    </div>
  );
};

export default JoinSession;
