// LoginForm.js
import * as React from 'react';
import classNames from 'classnames';
import LoginTab from './LoginTab';
import RegisterTab from './RegisterTab';

export default class LoginForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      active: "login",
      firstName: "",
      lastName: "",
      login: "",
      password: "",
    };
  };

  onChangeHandler = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    this.setState({ [name]: value });
  };

  onSubmitLogin = (e) => {
    e.preventDefault(); // Prevent default form submission
    this.props.onLogin(e, this.state.login, this.state.password);
  };

  onSubmitRegister = (e) => {
    e.preventDefault(); // Prevent default form submission
    this.props.onRegister(e, this.state.firstName, this.state.lastName, this.state.login, this.state.password);
  };

  render() {
    return (
      <div className="row justify-content-center">
        <div className="col-4">
          <ul className="nav nav-pills nav-justified mb-3" id="ex1" role="tablist">
            <li className="nav-item" role="presentation">
              <button
                className={classNames("nav-link", this.state.active === "login" ? "active" : "")}
                id="tab-login"
                onClick={() => this.setState({ active: "login" })}>
                Login
              </button>
            </li>
            <li className="nav-item" role="presentation">
              <button
                className={classNames("nav-link", this.state.active === "register" ? "active" : "")}
                id="tab-register"
                onClick={() => this.setState({ active: "register" })}>
                Register
              </button>
            </li>
          </ul>

          <div className="tab-content">
            {this.state.active === "login" && (
              <LoginTab onChange={this.onChangeHandler} onSubmit={this.onSubmitLogin} />
            )}
            {this.state.active === "register" && (
              <RegisterTab onChange={this.onChangeHandler} onSubmit={this.onSubmitRegister} />
            )}
          </div>
        </div>
      </div>
    );
  };
}
