import * as React from 'react';
import { request, setAuthHeader } from '../../helpers/axios_helper';
import LoginPage from './LoginPage';  // Admin login page
import RegisterPage from './RegisterPage';  // Admin register page

export default class AdminContent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      componentToShow: 'login',  // Start with login
      loading: false,  // To manage loading state
    };
  }

  onLogin = (e, username, password) => {
    e.preventDefault();
    this.setState({ loading: true });

    request('POST', '/login', { login: username, password: password })
      .then((response) => {
        setAuthHeader(response.data.token);  // Set token after successful login
        this.setState({ componentToShow: 'adminOptions', loading: false });  // Show admin options after login
      })
      .catch(() => {
        setAuthHeader(null);
        this.setState({ loading: false });  // Handle error (show error message if needed)
      });
  };

  onRegister = (e, firstName, lastName, username, password) => {
    e.preventDefault();
    this.setState({ loading: true });

    request('POST', '/register', { firstName, lastName, login: username, password: password })
      .then((response) => {
        setAuthHeader(response.data.token);  // Set token after successful registration
        this.setState({ componentToShow: 'adminOptions', loading: false });  // Return to options after adding admin
      })
      .catch(() => {
        setAuthHeader(null);
        this.setState({ loading: false });  // Handle error (show error message if needed)
      });
  };

  handleRemoveAdmin = () => {
    // Placeholder for removing admin logic
    alert('Remove Admin functionality not yet implemented');
  };

  handleViewAdmins = () => {
    // Placeholder for viewing admins logic
    alert('View Admins functionality not yet implemented');
  };

  // Back to home function to reset to welcome page or admin options
  handleBackToHome = () => {
    this.setState({ componentToShow: 'adminOptions' });
  };

  renderAdminOptions = () => {
    return (
      <div>
        <h2>Admin Options</h2>
        <ul>
          <li>
            <button onClick={() => this.setState({ componentToShow: 'register' })}>
              Add Admin
            </button>
          </li>
          <li>
            <button onClick={this.handleRemoveAdmin}>
              Remove Admin
            </button>
          </li>
          <li>
            <button onClick={this.handleViewAdmins}>
              View Admins
            </button>
          </li>
        </ul>
      </div>
    );
  };

  render() {
    const { componentToShow, loading } = this.state;

    return (
      <>
        {loading && <p>Loading...</p>} {/* Show a loading message when needed */}
        {componentToShow === 'login' && (
          <LoginPage 
            onLogin={this.onLogin} 
            onBack={this.handleBackToHome}  // Handle back from login page
          />
        )}
        {componentToShow === 'register' && (
          <RegisterPage 
            onRegister={this.onRegister} 
            onBack={this.handleBackToHome}  // Handle back from register page
          />
        )}
        {componentToShow === 'adminOptions' && this.renderAdminOptions()}
      </>
    );
  }
}
