import * as React from 'react';
import { request, setAuthHeader } from '../../helpers/axios_helper';
import LoginPage from './LoginPage';  // Admin login page
import RegisterPage from './RegisterPage';  // Admin register page
import AppContent from '../AppContent';  // The main app content (home page)

export default class AdminContent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      componentToShow: 'login',  // Start with login
      loading: false,  // To manage loading state
      editors: [],  // To store the list of editors
      viewers: [],  // To store the list of viewers
    };
  }

  onLogin = (e, username, password) => {
    e.preventDefault();
    this.setState({ loading: true });

    request('POST', '/admin/login', { login: username, password: password })
      .then((response) => {
        setAuthHeader(response.data.token);  // Set token after successful login
        this.setState({ componentToShow: 'adminOptions', loading: false });  // Show admin options after login
      })
      .catch(() => {
        setAuthHeader(null);
        this.setState({ loading: false });  // Handle error (show error message if needed)
      });
  };

  onRegisterEditor = (e, firstName, lastName, username, password) => {
    e.preventDefault();
    this.setState({ loading: true });

    request('POST', '/admin/registerEditor', { firstName, lastName, login: username, password: password })
      .then((response) => {
        setAuthHeader(response.data.token);  // Set token after successful registration
        this.setState({ componentToShow: 'adminOptions', loading: false });  // Return to options after adding admin
      })
      .catch(() => {
        setAuthHeader(null);
        this.setState({ loading: false });  // Handle error (show error message if needed)
      });
  };

  onRegisterViewer = (e, firstName, lastName, username, password) => {
    e.preventDefault();
    this.setState({ loading: true });

    request('POST', '/admin/registerViewer', { firstName, lastName, login: username, password: password })
      .then((response) => {
        setAuthHeader(response.data.token);  // Set token after successful registration
        this.setState({ componentToShow: 'adminOptions', loading: false });  // Return to options after adding admin
      })
      .catch(() => {
        setAuthHeader(null);
        this.setState({ loading: false });  // Handle error (show error message if needed)
      });
  };

  handleRemoveEditor = () => {
    const editorId = prompt("Enter the editor ID to remove:");
    if (editorId) {
      this.setState({ loading: true });

      request('POST', `/admin/removeEditor/${editorId}`)
        .then(() => {
          alert(`Editor with ID ${editorId} removed successfully.`);
          this.setState({ loading: false });
        })
        .catch((error) => {
          alert(`Error removing editor: ${error.response?.data || error.message}`);
          this.setState({ loading: false });
        });
    }
  };

  handleViewEditors = () => {
    this.setState({ loading: true }); // Set loading state while fetching editors

    request('GET', '/admin/viewEditor')
      .then((response) => {
        this.setState({ editors: response.data, componentToShow: 'viewEditors', loading: false }); // Store editors and change the component
      })
      .catch((error) => {
        alert(`Error fetching editors: ${error.response?.data || error.message}`);
        this.setState({ loading: false });
      });
  };

  handleViewers = () => {
    this.setState({ loading: true }); // Set loading state while fetching viewers

    request('GET', '/admin/viewViewer')  // Make sure to use the correct API endpoint for viewers
      .then((response) => {
        this.setState({ viewers: response.data, componentToShow: 'viewViewers', loading: false }); // Store viewers and change the component
      })
      .catch((error) => {
        alert(`Error fetching viewers: ${error.response?.data || error.message}`);
        this.setState({ loading: false });
      });
  };

  handleRemoveViewer = () => {
    const viewerId = prompt("Enter the viewer ID to remove:");
    if (viewerId) {
      this.setState({ loading: true });

      request('POST', `/admin/removeViewer/${viewerId}`) // Make sure to use the correct API endpoint for viewers
        .then(() => {
          alert(`Viewer with ID ${viewerId} removed successfully.`);
          this.setState({ loading: false });
        })
        .catch((error) => {
          alert(`Error removing viewer: ${error.response?.data || error.message}`);
          this.setState({ loading: false });
        });
    }
  };

  handleBackToAdminOptions = () => {
    this.setState({ componentToShow: 'adminOptions' });
  };

  handleBackToHome = () => {
    this.setState({ componentToShow: 'loginRoles' });
  };

  handleLogout = () => {
    this.setState({ componentToShow: 'loginRoles' });
  };

  // Add method to show editor management options
  handleManageEditors = () => {
    this.setState({ componentToShow: 'editorManagement' });
  };

  // Add method to show viewer management options
  handleManageViewers = () => {
    this.setState({ componentToShow: 'viewerManagement' });
  };

  renderAdminOptions = () => {
    return (
      <div>
        <h2>Admin Options</h2>
        <ul>
          <li>
            <button onClick={this.handleManageEditors}>
              Manage Editors
            </button>
          </li>
          <li>
            <button onClick={this.handleManageViewers}>
              Manage Viewers
            </button>
          </li>
          <li>
            <button onClick={this.handleLogout}>
              Logout
            </button>
          </li>
        </ul>
      </div>
    );
  };

  renderEditorsManagement = () => {
    return (
      <div>
        <h2>Manage Editors</h2>
        <button onClick={() => this.setState({ componentToShow: 'registerEditor' })}>
          Add Editor
        </button>
        <button onClick={this.handleRemoveEditor}>
          Remove Editor
        </button>
        <button onClick={this.handleViewEditors}>
          View Editors
        </button>
        <button onClick={this.handleBackToAdminOptions}>
          Back to Admin Options
        </button>
      </div>
    );
  };

  renderViewersManagement = () => {
    return (
      <div>
        <h2>Manage Viewers</h2>
        <button onClick={() => this.setState({ componentToShow: 'registerViewer' })}>
          Add Viewer
        </button>
        <button onClick={this.handleRemoveViewer}>
          Remove Viewer
        </button>
        <button onClick={this.handleViewers}>
          View Viewers
        </button>
        <button onClick={this.handleBackToAdminOptions}>
          Back to Admin Options
        </button>
      </div>
    );
  };

  renderEditorsList = () => {
    return (
      <div>
        <h2>Editors List</h2>
        <ul>
          {this.state.editors.map(editor => (
            <li key={editor.id}>{editor.firstName} {editor.lastName} (ID: {editor.id})</li>
          ))}
        </ul>
        <button onClick={this.handleBackToAdminOptions}>
          Back to Admin Options
        </button>
      </div>
    );
  };

  renderViewersList = () => {
    return (
      <div>
        <h2>Viewers List</h2>
        <ul>
          {this.state.viewers.map(viewer => (
            <li key={viewer.id}>{viewer.firstName} {viewer.lastName} (ID: {viewer.id})</li>
          ))}
        </ul>
        <button onClick={this.handleBackToAdminOptions}>
          Back to Admin Options
        </button>
      </div>
    );
  };

  render() {
    const { componentToShow, loading } = this.state;

    return (
      <>
        {loading && <p>Loading...</p>} {/* Show a loading message when needed */}
        {componentToShow === 'login' && (
          <LoginPage 
            onLogin={this.onLogin} 
            onBack={this.handleBackToHome}  // Handle back from login page
          />
        )}
        {componentToShow === 'registerEditor' && (
          <RegisterPage 
            onRegister={this.onRegisterEditor} 
            onBack={this.handleBackToAdminOptions}  // Handle back from register page
          />
        )}
        
        {componentToShow === 'registerViewer' && (
          <RegisterPage 
            onRegister={this.onRegisterViewer} 
            onBack={this.handleBackToAdminOptions}  // Handle back from register page
          />
        )}
        {componentToShow === 'adminOptions' && this.renderAdminOptions()}
        {componentToShow === 'editorManagement' && this.renderEditorsManagement()} {/* Render editor management options */}
        {componentToShow === 'viewerManagement' && this.renderViewersManagement()} {/* Render viewer management options */}
        {componentToShow === 'viewEditors' && this.renderEditorsList()}
        {componentToShow === 'viewViewers' && this.renderViewersList()} {/* Render viewers list */}
        {componentToShow === 'loginRoles' && <AppContent />}  {/* Render AppContent when going back */}
      </>
    );
  }
}
