import React from 'react';
import classNames from 'classnames';

const AuthForms = ({ active, onChange, onLogin, onRegister }) => {
    const handleLogin = (e) => {
        e.preventDefault();
        onLogin(e, e.target.login.value, e.target.password.value);
    };

    const handleRegister = (e) => {
        e.preventDefault();
        onRegister(e, e.target.firstName.value, e.target.lastName.value, e.target.login.value, e.target.password.value);
    };

    return (
        <div>
            <ul className="nav nav-pills nav-justified mb-3" id="ex1" role="tablist">
                <li className="nav-item" role="presentation">
                    <button className={classNames("nav-link", active === "login" ? "active" : "")} onClick={() => onChange("login")}>Login</button>
                </li>
                <li className="nav-item" role="presentation">
                    <button className={classNames("nav-link", active === "register" ? "active" : "")} onClick={() => onChange("register")}>Register</button>
                </li>
            </ul>

            {active === 'login' && (
                <form onSubmit={handleLogin}>
                    <div className="form-outline mb-4">
                        <input type="text" name="login" className="form-control" required />
                        <label className="form-label">Username</label>
                    </div>
                    <div className="form-outline mb-4">
                        <input type="password" name="password" className="form-control" required />
                        <label className="form-label">Password</label>
                    </div>
                    <button type="submit" className="btn btn-primary btn-block mb-4">Sign in</button>
                </form>
            )}

            {active === 'register' && (
                <form onSubmit={handleRegister}>
                    <div className="form-outline mb-4">
                        <input type="text" name="firstName" className="form-control" required />
                        <label className="form-label">First name</label>
                    </div>
                    <div className="form-outline mb-4">
                        <input type="text" name="lastName" className="form-control" required />
                        <label className="form-label">Last name</label>
                    </div>
                    <div className="form-outline mb-4">
                        <input type="text" name="login" className="form-control" required />
                        <label className="form-label">Username</label>
                    </div>
                    <div className="form-outline mb-4">
                        <input type="password" name="password" className="form-control" required />
                        <label className="form-label">Password</label>
                    </div>
                    <button type="submit" className="btn btn-primary btn-block mb-3">Sign up</button>
                </form>
            )}
        </div>
    );
};

export default AuthForms;
