// src/components/WelcomeContent.js
import React from 'react';
import { useNavigate } from 'react-router-dom';

const WelcomeContent = () => {
  const navigate = useNavigate();

  const handleRoleSelect = (role) => {
    // Navigate to the appropriate route based on the selected role
    if (role === 'admin') {
      navigate('/admin');
    } else if (role === 'editor') {
      navigate('/editor'); // Assuming you'll create an EditorContent component
    } else if (role === 'viewer') {
      navigate('/viewer'); // Assuming you'll create a ViewerContent component
    }
  };

  return (
    <div className="jumbotron">
      <h1 className="display-4">Welcome to Collaborative Code Editor</h1>
      <h2 className="lead">Choose your role:</h2>
      <div className="mt-4">
        <button className="btn btn-primary mx-2" onClick={() => handleRoleSelect('admin')}>
          Admin
        </button>
        <button className="btn btn-secondary mx-2" onClick={() => handleRoleSelect('editor')}>
          Editor
        </button>
        <button className="btn btn-success mx-2" onClick={() => handleRoleSelect('viewer')}>
          Viewer
        </button>
      </div>
    </div>
  );
};

export default WelcomeContent;
