import React, { useState } from 'react';
import RegisterPage from './RegisterPage';
import RegisterViewerPage from './RegisterViewerPage';
import { request, setAuthHeader } from './apiService'; // Adjust import based on your API service file path

const AdminContent = () => {
  const [componentToShow, setComponentToShow] = useState('adminOptions');
  const [loading, setLoading] = useState(false);
  
  const onRegister = (e, firstName, lastName, username, password) => {
    e.preventDefault();
    setLoading(true);

    request('POST', '/admin/register', { firstName, lastName, login: username, password })
      .then((response) => {
        setAuthHeader(response.data.token);
        setComponentToShow('adminOptions');
        setLoading(false);
        alert('Editor registered successfully!');
      })
      .catch((error) => {
        setAuthHeader(null);
        setLoading(false);
        alert(`Registration failed: ${error.response?.data || error.message}`);
      });
  };

  const onRegisterViewer = (e, firstName, lastName, username, password) => {
    e.preventDefault();
    setLoading(true);

    request('POST', '/admin/registerViewer', { firstName, lastName, login: username, password })
      .then((response) => {
        setAuthHeader(response.data.token);
        setComponentToShow('adminOptions');
        setLoading(false);
        alert('Viewer registered successfully!');
      })
      .catch((error) => {
        setAuthHeader(null);
        setLoading(false);
        alert(`Registration failed: ${error.response?.data || error.message}`);
      });
  };

  const renderAdminOptions = () => (
    <div>
      <h2>Admin Options</h2>
      <button onClick={() => setComponentToShow('register')}>Register Editor</button>
      <button onClick={() => setComponentToShow('registerViewer')}>Register Viewer</button>
      <button onClick={handleViewEditors}>View Editors</button>
      <button onClick={handleViewViewers}>View Viewers</button>
      <button onClick={handleRemoveEditor}>Remove Editor</button>
      <button onClick={handleRemoveViewer}>Remove Viewer</button>
      <button onClick={handleLogout}>Logout</button>
    </div>
  );

  const handleViewEditors = () => {
    // Implement fetching and displaying the list of editors
    setComponentToShow('viewEditors');
  };

  const handleViewViewers = () => {
    // Implement fetching and displaying the list of viewers
    setComponentToShow('viewViewers');
  };

  const handleRemoveEditor = () => {
    // Implement editor removal logic
    alert('Functionality to remove editor goes here.');
  };

  const handleRemoveViewer = () => {
    // Implement viewer removal logic
    alert('Functionality to remove viewer goes here.');
  };

  const handleLogout = () => {
    // Implement logout logic
    setAuthHeader(null);
    alert('Logged out successfully!');
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <>
      {componentToShow === 'register' && (
        <RegisterPage onRegister={onRegister} onBack={() => setComponentToShow('adminOptions')} />
      )}
      {componentToShow === 'registerViewer' && (
        <RegisterViewerPage onRegister={onRegisterViewer} onBack={() => setComponentToShow('adminOptions')} />
      )}
      {componentToShow === 'adminOptions' && renderAdminOptions()}
      {componentToShow === 'viewEditors' && (
        <div>
          <h2>Editors List</h2>
          {/* Fetch and display the list of editors here */}
          <button onClick={() => setComponentToShow('adminOptions')}>Back</button>
        </div>
      )}
      {componentToShow === 'viewViewers' && (
        <div>
          <h2>Viewers List</h2>
          {/* Fetch and display the list of viewers here */}
          <button onClick={() => setComponentToShow('adminOptions')}>Back</button>
        </div>
      )}
    </>
  );
};

export default AdminContent;
