import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import '../../CSS/CollaborativeRoom.css'; // Import your CSS file for styling
import { request, setAuthHeader } from '../../helpers/axios_helper'; 

const Collaborative = () => {
    const location = useLocation();
    const { project } = location.state || {}; // Access the passed project from state
    const [id, setId] = useState(null); // State for room ID
    const [password, setPassword] = useState(null); // State for password
    const [comments, setComments] = useState([]); // State for comments
    const [loading, setLoading] = useState(true); // Set loading state to true on component mount
    const username = localStorage.getItem('username'); // Retrieve the username from local storage

    useEffect(() => {
        const createRoom = async () => {
            const token = localStorage.getItem('auth_token'); // Fetch the auth token
            if (!token) {
                alert('Session expired. Please log in again.');
                return;
            }

            setAuthHeader(token); // Set the authorization header

            try {
                console.log("Creating room..."); // Log before the request
                const response = await request('post', '/room/create'); // Send POST request
                console.log("Response received:", response);

                // Check if the response contains the expected data
                if (response && response.data) {
                    const { password, roomId } = response.data; // Destructure password and roomId
                    console.log(`Room created with ID: ${roomId}, Password: ${password}`);

                    // Set these values in the component state
                    setId(roomId); // Update room ID state
                    setPassword(password); // Update password state
                } else {
                    console.error('Unexpected response format:', response);
                    setId(null); // Resetting the state in case of unexpected response
                    setPassword(null);
                }
            } catch (error) {
                console.error('Error creating room:', error);
                setId(null); // Resetting the state in case of error
                setPassword(null);
            } finally {
                setLoading(false); // Reset loading state
            }
        };

        const fetchComments = async () => {
            const token = localStorage.getItem('auth_token'); // Fetch the auth token
            if (!token) {
                alert('Session expired. Please log in again.');
                return;
            }
        
            setAuthHeader(token); // Set the authorization header
        
            try {
                console.log("Fetching project ID..."); // Log before the request
                const projectResponse = await request('GET', '/project/getId', { project_name: project }); // Fetch project ID
                console.log("Project ID response:", projectResponse);
        
                const projectId = projectResponse.data.id; // Get the project ID
        
                console.log("Fetching comments..."); // Log before the request
                const commentsResponse = await request('GET', '/comment/read', { project_id: projectId }); // Fetch comments
                console.log("Comments response:", commentsResponse);
        
                // Check if the response contains comments
                if (commentsResponse && commentsResponse.data && commentsResponse.data.comments) {
                    setComments(commentsResponse.data.comments); // Update comments state
                } else {
                    console.error('No comments found:', commentsResponse);
                    setComments([]); // Resetting the state in case of no comments
                }
            } catch (error) {
                console.error('Error fetching comments:', error);
                setComments([]); // Resetting the state in case of error
            }
        };
        
        createRoom(); // Call the function to create a room on component mount
        fetchComments(); // Call the function to fetch comments on component mount
    }, [project]); // Empty dependency array to run only once on mount

    return (
        <div className="collaborative-container">
            <div className="project-name-box">
                {project ? `Collaborate on project: ${project}` : 'No Project'}
            </div>
            <div className="id-box">
                {loading ? 'Creating room...' : id ? `ID: ${id}` : 'No ID'}
                <br />
                {loading ? '' : password ? `Password: ${password}` : 'No Password'}
            </div>
            <div className="comments-section">
                <h3>Comments</h3>
                {comments.length > 0 ? (
                    <ul>
                        {comments.map((comment, index) => (
                            <li key={index}>
                                <strong>{comment.name}</strong>: {comment.comment} <br />
                                <small>{comment.date} {comment.time}</small>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No comments found.</p>
                )}
            </div>
        </div>
    );
};

export default Collaborative;
