// src/components/AppContent.js
import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import WelcomeContent from './WelcomeContent';
import AdminContent from './admin/AdminContent';

export default class AppContent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      componentToShow: 'welcome', // Default to welcome screen
    };
  }

  onRoleSelect = (role) => {
    this.setState({ componentToShow: role });
  };

  render() {
    const { componentToShow } = this.state;

    return (
      <Routes>
        <Route path="/" element={<WelcomeContent onRoleSelect={this.onRoleSelect} />} />
        <Route path="/admin" element={componentToShow === 'admin' ? <AdminContent /> : <Navigate to="/" />} />
        {/* Add more routes for editor and viewer */}
      </Routes>
    );
  }
}
