// src/components/viewer/ViewerLogin.js
import React from 'react';
import LoginForm from '../LoginForm'; 
import { request, setAuthHeader } from '../../helpers/axios_helper';

const ViewerLogin = ({ onBack }) => {
  const onLogin = (e, username, password) => {
    e.preventDefault();

    request('POST', '/viewer/login', { login: username, password: password })
      .then((response) => {
        const token = response.data.token;
        localStorage.setItem('authToken', token);
        setAuthHeader(token);
        
        window.location.href = '/viewerContent'; 
      })
      .catch(() => {
        localStorage.removeItem('authToken');
        setAuthHeader(null);
        alert('Login failed. Please try again.');
      });
  };

  return (
    <div className="login-page">
      <h2>Viewer Login</h2>
      <LoginForm onLogin={onLogin} />
      <button className="btn btn-secondary mt-3" onClick={onBack}>
        Back to Home
      </button>
    </div>
  );
};

export default ViewerLogin;
