// src/components/AppContent.js
import React from 'react';
import AdminContent from './admin/AdminContent';  

export default class AppContent extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      componentToShow: 'welcome', 
    };
  }

  onRoleSelect = (role) => {
    if (role === 'admin') {
      this.setState({ componentToShow: 'admin' });
    } else {
      // Handle other roles like editor, viewer, etc. here
    }
  };

  onBackToWelcome = () => {
    this.setState({ componentToShow: 'welcome' });
  };

  render() {
    const { componentToShow } = this.state;

    return (
      <>
        {componentToShow === 'welcome' && (
          <div className="jumbotron">
            <h1 className="display-4">Welcome Collaborative Code Editor</h1>
            <h2 className="lead">Choose your role:</h2>
            <div className="mt-4">
              <button className="btn btn-primary mx-2" onClick={() => this.onRoleSelect('admin')}>
                Admin
              </button>
              <button className="btn btn-secondary mx-2" onClick={() => this.onRoleSelect('editor')}>
                Editor
              </button>
              <button className="btn btn-success mx-2" onClick={() => this.onRoleSelect('viewer')}>
                Viewer
              </button>
            </div>
          </div>
        )}

        {componentToShow === 'admin' && <AdminContent onBack={this.onBackToWelcome} />}
      </>
    );
  }
}
