import * as React from 'react';
import { request, setAuthHeader } from '../helpers/axios_helper';
import AuthContent from './AuthContent';
import WelcomeContent from './WelcomeContent';
import LoginPage from './LoginPage'; 

export default class AppContent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      componentToShow: 'welcome', 
      loading: false,
    };
  }

  login = () => {
    this.setState({ componentToShow: 'login' });
  };

  logout = () => {
    this.setState({ componentToShow: 'welcome' });
    setAuthHeader(null);
  };

  onRoleSelect = (role) => {
    if (role === 'admin') {
      this.setState({ componentToShow: 'login' }); 
    }
    // Add handling for 'editor' and 'viewer' roles if needed
  };

  onLogin = (e, username, password) => {
    e.preventDefault();
    this.setState({ loading: true });
    request('POST', '/login', {
      login: username,
      password: password,
    })
      .then((response) => {
        setAuthHeader(response.data.token);
        this.setState({ componentToShow: 'messages', loading: false });
      })
      .catch(() => {
        setAuthHeader(null);
        this.setState({ componentToShow: 'welcome', loading: false });
      });
  };

  onRegister = (event, firstName, lastName, username, password) => {
    event.preventDefault();
    this.setState({ loading: true });
    request('POST', '/register', {
      firstName: firstName,
      lastName: lastName,
      login: username,
      password: password,
    })
      .then((response) => {
        setAuthHeader(response.data.token);
        this.setState({ componentToShow: 'messages', loading: false });
      })
      .catch(() => {
        setAuthHeader(null);
        this.setState({ componentToShow: 'welcome', loading: false });
      });
  };

  render() {
    const { componentToShow } = this.state;

    return (
      <>
        {componentToShow === 'welcome' && <WelcomeContent onRoleSelect={this.onRoleSelect} />}
        {componentToShow === 'login' && (
          <LoginPage onLogin={this.onLogin}onBack={() => this.setState({ componentToShow: 'welcome' })} />
        )}
        {componentToShow === 'messages' && <AuthContent />}
      </>
    );
  }

}
