// src/components/AppContent.js
import React from 'react';
import WelcomeContent from './WelcomeContent';
import AdminContent from './admin/AdminContent'; // Admin-specific content

class AppContent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      componentToShow: 'welcome', // Default to welcome screen
    };
  }

  onRoleSelect = (role) => {
    if (role === 'admin') {
      // Call the navigate function passed from props
      this.props.navigate('/adminLogin');
    }
    // You can add other roles like editor, viewer, etc. here
  };

  render() {
    const { componentToShow } = this.state;

    return (
      <>
        {componentToShow === 'welcome' && <WelcomeContent onRoleSelect={this.onRoleSelect} />}
        {componentToShow === 'admin' && <AdminContent />}
      </>
    );
  }
}

export default AppContent;
