// AppContent.js
import React from 'react';
import WelcomeContent from './WelcomeContent';
import AdminContent from './admin/AdminContent';
import './AppContent.css'; // Import your CSS file

export default class AppContent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      componentToShow: 'welcome', // Default to welcome screen
    };
  }

  onRoleSelect = (role) => {
    if (role === 'admin') {
      this.setState({ componentToShow: 'admin' });
    }
    // You can add other roles like editor, viewer, etc. here
  };

  render() {
    const { componentToShow } = this.state;

    return (
      <div className="app-content"> {/* Apply the app-content class here */}
        {componentToShow === 'welcome' && <WelcomeContent onRoleSelect={this.onRoleSelect} />}
        {componentToShow === 'admin' && <AdminContent />} 
      </div>
    );
  }
}
