// src/components/JoinSession.js
import React, { useState, useEffect } from 'react';
import '../../CSS/JoinSession.css'; // Import the CSS file

const JoinSession = () => {
  const [sessionCode, setSessionCode] = useState('');
  const [projectName, setprojectName] = useState('');
  const [password, setPassword] = useState('');
  const username = localStorage.getItem('username');


  const [socket, setSocket] = useState(null); // For WebSocket connection

  useEffect(() => {
    const newSocket = new WebSocket('ws://localhost:8082/chat'); // Adjust URL as needed
    newSocket.onopen = () => {
      console.log('WebSocket connection established');
    };
    
    newSocket.onmessage = (message) => {
      console.log('Message from server:', message.data);
    };

    newSocket.onclose = () => {
      console.log('WebSocket connection closed');
    };

    setSocket(newSocket);

    // Clean up on component unmount
    return () => {
      newSocket.close();
    };
  }, []);

  const handleJoinSession = () => {
    const chatMessage = {
      sender: username,
      password: password,
    };

    // Send the message through WebSocket
    if (socket) {
      socket.send(JSON.stringify({ roomId: sessionCode, chatMessage }));
      console.log('Joined session:', sessionCode);
      console.log('Password:', password);
      console.log('Username:', username);
      
    } else {
      console.error('WebSocket is not connected');
    }
  };
  return (
    <div className="join-session-container"> 
      <h2>Join a Room</h2>
      <input
        type="text"
        placeholder="Enter room ID"
        value={sessionCode}
        onChange={(e) => setSessionCode(e.target.value)}
      />
      <input
        type="password"
        placeholder="Enter room password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
           <input
        type="text"
        placeholder="Enter project name"
        value={projectName}
        onChange={(e) => setprojectName(e.target.value)}
      />
      <button onClick={handleJoinSession}>Join Session</button>
    </div>
  );
};

export default JoinSession;
