import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { request, setAuthHeader } from '../../helpers/axios_helper'; // Make sure to import your request function

const SelectExistingProject = () => {
    const location = useLocation();
    const { language } = location.state || {}; // Access the passed language from state
    const [projects, setProjects] = useState([]); // State to hold the project names
    const [loading, setLoading] = useState(false); // State for loading
    const [error, setError] = useState(null); // State for error handling

    useEffect(() => {
        if (language) {
            fetchProjects();
        }
    }, [language]);

    // Function to fetch projects based on the selected language
    const fetchProjects = async () => {
        setLoading(true); // Set loading state to true
        const token = localStorage.getItem('authToken');
        if (!token) {
            alert('Session expired. Please log in again.');
            return;
        }

        setAuthHeader(token); // Set the authorization header

        try {
            console.log("Fetching projects for language:", language);
            const response = await request('GET', `/project?language=${language}`); // Make the API call
            console.log("Response received:", response); // Log the response
            setProjects(response.projects || []); // Set the projects state
        } catch (error) {
            console.error('Error fetching projects:', error);
            setError('Failed to fetch projects.'); // Set error message
        } finally {
            setLoading(false); // Reset loading state
        }
    };

    return (
        <div>
            <h2>Select Existing Project</h2>
            {loading && <p>Loading projects...</p>}
            {error && <p>{error}</p>}
            {language ? (
                <div>
                    <p>Selected Language: {language}</p>
                    {projects.length > 0 ? (
                        <ul>
                            {projects.map((project, index) => (
                                <li key={index}>{project}</li> // Render project names
                            ))}
                        </ul>
                    ) : (
                        <p>No projects found for the selected language.</p>
                    )}
                </div>
            ) : (
                <p>No language selected.</p>
            )}
        </div>
    );
};

export default SelectExistingProject;
