import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { login } from '../../services/loginService'; // Import the login function for viewer

const ViewerLogin = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const success = await login(username, password); // Assuming login service handles viewer login too

    if (success) {
      navigate('/viewer'); // Navigate to the viewer content page (adjust route as needed)
    } else {
      alert('Login failed. Please try again.');
    }
  };

  return (
    <div className="login-container">
      <h2>Viewer Login</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="username">Username:</label>
          <input 
            type="text" 
            id="username" 
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required 
          />
        </div>

        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input 
            type="password" 
            id="password" 
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required 
          />
        </div>

        <button type="submit" className="login-button">Login</button>
      </form>
    </div>
  );
};

export default ViewerLogin;
