import React from 'react';
import LoginForm from './LoginForm';
import { login } from '../services/loginService'; // Import your login service

class LoginPage extends React.Component {
  handleLogin = async (e, username, password) => {
    e.preventDefault();
    
    const success = await login(username, password);

    if (success) {
      this.props.history.push('/admin-content'); 
    } else {
      alert('Login failed. Please try again.'); // Show an alert for login failure
    }
  };

  render() {
    return (
      <div>
        <h2>Login</h2>
        <LoginForm onLogin={this.handleLogin} />
      </div>
    );
  }
}

export default LoginPage;
