import * as React from 'react';
import { request, setAuthHeader } from '../helpers/axios_helper';
import Buttons from './Buttons';
import AuthContent from './AuthContent';
import LoginForm from './LoginForm';
import WelcomeContent from './WelcomeContent';

export default class AppContent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      componentToShow: 'welcome',
      loading: false,
    };
  }


  onLogin = (e, username, password) => {
    e.preventDefault();
    this.setState({ loading: true });
    request('POST', '/login', {
      login: username,
      password: password,
    })
      .then((response) => {
        setAuthHeader(response.data.token);
        this.setState({ componentToShow: 'messages', loading: false });
      })
      .catch(() => {
        setAuthHeader(null);
        this.setState({ componentToShow: 'welcome', loading: false });
      });
  };

  onRegister = (event, firstName, lastName, username, password) => {
    event.preventDefault();
    this.setState({ loading: true });
    request('POST', '/register', {
      firstName: firstName,
      lastName: lastName,
      login: username,
      password: password,
    })
      .then((response) => {
        setAuthHeader(response.data.token);
        this.setState({ componentToShow: 'messages', loading: false });
      })
      .catch(() => {
        setAuthHeader(null);
        this.setState({ componentToShow: 'welcome', loading: false });
      });
  };

  render() {
    const { componentToShow, loading } = this.state;

    return (
      <>
        <Buttons login={this.login} logout={this.logout} />
        {loading && <div className="spinner-border" role="status" />}
        {componentToShow === 'welcome' && <WelcomeContent />}
        {componentToShow === 'login' && (
          <LoginForm onLogin={this.onLogin} onRegister={this.onRegister} />
        )}
        {componentToShow === 'messages' && <AuthContent />}
      </>
    );
  }
}
