// src/components/CreateSession.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../../CSS/CreateSession.css';
import { request, setAuthHeader } from '../../helpers/axios_helper'; 

const CreateSession = () => {
    const [projectType, setProjectType] = useState('');
    const [languages, setLanguages] = useState([]); // State to store languages
    const [loading, setLoading] = useState(false); // State for loading
    const navigate = useNavigate();

    // Fetch languages from the API when projectType changes
    useEffect(() => {
        if (projectType) {
            fetchLanguages();
        }
    }, [projectType]);

    // Custom request function to fetch languages
    const fetchLanguages = async () => {
        const token = localStorage.getItem('authToken');
        if (!token) {
            alert('Session expired. Please log in again.');
            return;
        }
  
        setAuthHeader(token); // Set the authorization header
        setLoading(true); // Set loading state to true
  
        try {
            console.log("Fetching languages..."); // Log before fetching
            const response = await request('GET', '/project/languages'); // Using your custom request
            console.log("Response received:", response); // Print the entire response object

            // Access the languages array properly
            if (response && response.data && response.data.languages) {
                setLanguages(response.data.languages); // Access languages properly
                console.log("Languages set:", response.data.languages); // Log the languages set
            } else {
                console.error('Unexpected response format:', response);
                setLanguages([]); // Reset languages if the format is not as expected
            }
        } catch (error) {
            console.error('Error fetching languages:', error);
            setLanguages([]); // Reset languages on error
        } finally {
            setLoading(false); // Reset loading state
        }
    };

    const handleLanguageSelection = (language) => {
        console.log(`Selected language: ${language}`);
        navigate(`/select-existing-project/`); 
    };

    return (
        <div className="create-session-container">
            <h2>Create New Session</h2>
            <div className="project-selection">
                <button className="project-button" onClick={() => setProjectType('new')}>New Project</button>
                <button className="project-button" onClick={() => setProjectType('existing')}>Existing Project</button>
            </div>

            {projectType && (
                <div className="selected-project">
                    {loading ? ( 
                        <p>Loading languages...</p>
                    ) : languages.length > 0 ? (
                        <div className="language-list">
                            <h4>Languages:</h4>
                            <div className="language-buttons">
                                {languages.map((language, index) => (
                                    <button 
                                        key={index} 
                                        className="language-button" 
                                        onClick={() => handleLanguageSelection(language)}
                                    >
                                        {language}
                                    </button>
                                ))}
                            </div>
                        </div>
                    ) : (
                        <p>No languages available.</p>
                    )}
                </div>
            )}
        </div>
    );
};

export default CreateSession;
