// src/components/AppContent.js
import * as React from 'react';
import WelcomeContent from './WelcomeContent';
import AdminContent from './admin/AdminContent';  

export default class AppContent extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      componentToShow: 'welcome',  // Default to welcome screen
    };
  }

  onRoleSelect = (role) => {
    if (role === 'admin') {
      this.setState({ componentToShow: 'admin' });
    } else {
      // Handle other roles like editor, viewer, etc. here
    }
  };

  // Method to go back to WelcomeContent
  onBackToWelcome = () => {
    this.setState({ componentToShow: 'welcome' });
  };

  render() {
    const { componentToShow } = this.state;

    return (
      <>
        {componentToShow === 'welcome' && <WelcomeContent onRoleSelect={this.onRoleSelect} />}
        {componentToShow === 'admin' && <AdminContent onBack={this.onBackToWelcome} />} 
        {/* Pass down the back function to AdminContent */}
      </>
    );
  }
}
