// src/components/AppContent.js
import React from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for navigation
import WelcomeContent from './WelcomeContent';
import AdminContent from './admin/AdminContent';  // Admin-specific content

const AppContent = () => {
  const navigate = useNavigate(); // Create navigate function
  const [componentToShow, setComponentToShow] = React.useState('welcome'); // Default to welcome screen

  const onRoleSelect = (role) => {
    if (role === 'admin') {
      navigate('/adminLogin'); // Redirect to the admin login page
    }
    // You can add other roles like editor, viewer, etc. here
  };

  return (
    <>
      {componentToShow === 'welcome' && <WelcomeContent onRoleSelect={onRoleSelect} />}
      {componentToShow === 'admin' && <AdminContent />} 
    </>
  );
};

export default AppContent;
