import * as React from 'react';
import { request, setAuthHeader } from '../helpers/axios_helper';
import AuthContent from './AuthContent';
import WelcomeContent from './WelcomeContent';
import AuthForms from './AuthForms'; // Import the new component

export default class AppContent extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            componentToShow: 'welcome',
            loading: false,
            active: 'login' // Track active tab
        };
    }

    onRoleSelect = (role) => {
        if (role === 'admin') {
            this.setState({ componentToShow: 'auth' }); 
        }
    };

    onLogin = (e, username, password) => {
        e.preventDefault();
        this.setState({ loading: true });
        request('POST', '/login', { login: username, password })
            .then((response) => {
                setAuthHeader(response.data.token);
                this.setState({ componentToShow: 'messages', loading: false });
            })
            .catch(() => {
                setAuthHeader(null);
                this.setState({ componentToShow: 'welcome', loading: false });
            });
    };

    onRegister = (event, firstName, lastName, username, password) => {
        event.preventDefault();
        this.setState({ loading: true });
        request('POST', '/register', { firstName, lastName, login: username, password })
            .then((response) => {
                setAuthHeader(response.data.token);
                this.setState({ componentToShow: 'messages', loading: false });
            })
            .catch(() => {
                setAuthHeader(null);
                this.setState({ componentToShow: 'welcome', loading: false });
            });
    };

    render() {
        const { componentToShow, active } = this.state;

        return (
            <>
                {componentToShow === 'welcome' && <WelcomeContent onRoleSelect={this.onRoleSelect} />}
                {componentToShow === 'auth' && (
                    <>
                        <AuthContent />
                        <AuthForms 
                            active={active} 
                            onChange={(newActive) => this.setState({ active: newActive })} 
                            onLogin={this.onLogin} 
                            onRegister={this.onRegister} 
                        />
                    </>
                )}
                {componentToShow === 'messages' && <AuthContent />}
            </>
        );
    }
}
