import * as React from 'react';

export default class WelcomeContent extends React.Component {

  render() {
    return (
        <div className="jumbotron jumbotron-fluid">
          <h1 className="display-4">Welcome Collaborative Code Editor</h1>
          <p className="lead">Please select your role:</p>

          <div className="row justify-content-center">
            <button 
              className="btn btn-primary" 
              onClick={() => this.props.handleRoleSelection('admin')} 
              style={{ margin: '10px' }}>
              Admin
            </button>
            <button 
              className="btn btn-secondary" 
              onClick={() => this.props.handleRoleSelection('editor')} 
              style={{ margin: '10px' }}>
              Editor
            </button>
            <button 
              className="btn btn-info" 
              onClick={() => this.props.handleRoleSelection('viewer')} 
              style={{ margin: '10px' }}>
              Viewer
            </button>
          </div>
        </div>
    );
  }
}
