import * as React from 'react';
import Buttons from './Buttons';
import AuthContent from './AuthContent';
import LoginForm from './LoginForm';
import WelcomeContent from './WelcomeContent';

export default class AppContent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      componentToShow: 'welcome',
      role: null, // Track selected role
      loading: false,
    };
  }

  handleRoleSelection = (role) => {
    if (role === 'admin') {
      this.setState({ componentToShow: 'login', role: 'admin' });
    } 
    // You can add similar logic for 'editor' and 'viewer' later
  };

  onLogin = (e, username, password) => {
    e.preventDefault();
    this.setState({ loading: true });
    request('POST', '/login', {
      login: username,
      password: password,
    })
      .then((response) => {
        setAuthHeader(response.data.token);
        this.setState({ componentToShow: 'messages', loading: false });
      })
      .catch(() => {
        setAuthHeader(null);
        this.setState({ componentToShow: 'welcome', loading: false });
      });
  };

  render() {
    const { componentToShow, loading, role } = this.state;

    return (
      <>
        {componentToShow === 'welcome' && (
          <WelcomeContent handleRoleSelection={this.handleRoleSelection} />
        )}

        {loading && <div className="spinner-border" role="status" />}

        {componentToShow === 'login' && (
          <LoginForm 
            onLogin={this.onLogin} 
            role={role} // Pass role to LoginForm if needed
          />
        )}

        {componentToShow === 'messages' && <AuthContent />}
      </>
    );
  }
}
