import * as React from 'react';
import { request, setAuthHeader } from '*/helpers/axios_helper';
import LoginPage from './LoginPage';  // Admin login page
import RegisterPage from './RegisterPage';  // Admin register page

export default class AdminContent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      componentToShow: 'login',  // Start with login
      loading: false,  // To manage loading state
    };
  }

  onLogin = (e, username, password) => {
    e.preventDefault();
    this.setState({ loading: true });

    request('POST', '/login', { login: username, password: password })
      .then((response) => {
        setAuthHeader(response.data.token);  // Set token after successful login
        this.setState({ componentToShow: 'register', loading: false });  // Move to register page after login
      })
      .catch(() => {
        setAuthHeader(null);
        this.setState({ loading: false });  // Handle error (show error message if needed)
      });
  };

  onRegister = (e, firstName, lastName, username, password) => {
    e.preventDefault();
    this.setState({ loading: true });

    request('POST', '/register', { firstName, lastName, login: username, password: password })
      .then((response) => {
        setAuthHeader(response.data.token);  // Set token after successful registration
        // Handle post-registration (e.g., redirect or show a message)
      })
      .catch(() => {
        setAuthHeader(null);
        this.setState({ loading: false });  // Handle error (show error message if needed)
      });
  };

  render() {
    const { componentToShow, loading } = this.state;

    return (
      <>
        {loading && <p>Loading...</p>} {/* Show a loading message when needed */}
        {componentToShow === 'login' && <LoginPage onLogin={this.onLogin} />}
        {componentToShow === 'register' && <RegisterPage onRegister={this.onRegister} />}
      </>
    );
  }
}
